function showHideToggledContent(){$(".js-has-toggle").each(function(){var o=$(this),t=o.attr("data-target");o.on("click",function(){var o=$(this).closest(".js-toggle-group"),a=o.find(".js-has-toggle");a.each(function(){var o=$(this).attr("data-target"),t=$("#"+o);t.hide()});var n=$("#"+t);n.show()})})}function animateScroll(){$("a.js-animate-scroll").on("click",function(o){var t=$($(this).attr("href"));t.length&&(o.preventDefault(),$("body").addClass("is-scrolling"),$("html, body").animate({scrollTop:t.offset().top},500,function(){$("body").removeClass("is-scrolling")}))})}function accordion(){$(".accordion").each(function(){var o=$(this),t=o.find(".accordion__expand"),a=o.find(".accordion__collapse");t.on("click",function(o){var t=$(this).closest(".accordion__row");t.toggleClass("is-collapsed is-expanded"),o.preventDefault()}),a.on("click",function(o){var t=$(this).closest(".accordion__row");t.toggleClass("is-collapsed is-expanded"),o.preventDefault()})}),$(".accord").each(function(){var o=$(this),t=o.find(".accord__head__buttons");t.on("click",function(o){var t=$(this).closest(".accord__row");t.toggleClass("is-collapsed"),o.preventDefault()})})}function initPage(){console.log("page loaded"),showHideToggledContent(),animateScroll(),accordion()}